class TranslocoDirective implements OnInit, OnDestroy, OnChanges {
  subscription: Subscription | null;
  view: EmbeddedViewRef<any>;
  @Input("transloco") key: string;
  @Input("translocoParams") params: HashMap = {};
  @Input("translocoScope") inlineScope: string | undefined;
  @Input("translocoRead") inlineRead: string | undefined;
  @Input("translocoLang") inlineLang: string | undefined;
  @Input("translocoLoadingTpl") inlineTpl: TemplateRef<any> | undefined;

  ngOnInit();
  ngOnChanges(changes);
  ngOnDestroy();
}

class TranslocoPipe implements PipeTransform, OnDestroy {
  transform(
    key: string | null,
    params?: HashMap | undefined,
    inlineLang?: string | undefined
  ): string;
  ngOnDestroy();
}

const defaultProviders: (
  | { provide: any; useClass: typeof DefaultTranspiler; deps: any[] }
  | { provide: any; useClass: typeof DefaultHandler; deps?: undefined }
  | { provide: any; useClass: typeof DefaultInterceptor; deps?: undefined }
  | { provide: any; useClass: typeof DefaultFallbackStrategy; deps: any[] }
)[];

export type TranslocoConfig = {
  defaultLang: string;
  reRenderOnLangChange?: boolean;
  prodMode?: boolean;
  fallbackLang?: string | string[];
  failedRetries?: number;
  /** @deprecated */
  scopeMapping?: HashMap<string>;
  availableLangs?: AvailableLangs;
  flatten?: {
    aot?: boolean;
  };
  missingHandler?: {
    logMissingKey?: boolean;
    useFallbackTranslation?: boolean;
    allowEmpty?: boolean;
  };
  interpolation?: [string, string];
};

function provideTranslocoConfig(
  config: Partial<TranslocoConfig> = defaultConfig
): Provider;

function translocoConfig(
  config: Partial<TranslocoConfig> = defaultConfig
): TranslocoConfig;

class DefaultTranspiler implements TranslocoTranspiler {
  transpile(value: any, params: HashMap = {}, translation: Translation): any;
}

class FunctionalTranspiler
  extends DefaultTranspiler
  implements TranslocoTranspiler {
  transpile(value: any, params: HashMap = {}, translation: Translation): any;
}

export type FailedEvent = {
  type: "translationLoadFailure";
  payload: LoadedEvent["payload"];
};

export type LoadedEvent = {
  type: "translationLoadSuccess";
  wasFailure: boolean;
  payload: {
    scope: string | null;
    langName: string;
    /** @deprecated */
    lang: string;
  };
};

class TestingLoader implements TranslocoLoader {
  getTranslation(lang: string): Observable<Translation> | Promise<Translation>;
}

class TranslocoTestingModule {
  static withLangs(
    langs: HashMap<Translation>,
    config: Partial<TranslocoConfig> = {}
  ): ModuleWithProviders<TranslocoTestingModule>;
}

class DefaultFallbackStrategy implements TranslocoFallbackStrategy {
  getNextLangs(failedLang: string);
}

export type ProviderScope = {
  scope: string;
  loader?: InlineLoader;
  alias?: string;
};

function getPipeValue(
  str: string,
  value: string,
  char = "|"
): [boolean, string];

export type TranslateObjectParams =
  | TranslateParams
  | HashMap
  | Map<string, HashMap>;

export type LoadOptions = {
  fallbackLangs?: string[] | null;
  inlineLoader?: InlineLoader | null;
};

class TranslocoLocaleService implements OnDestroy {
  localeChanges$: Observable<Locale>;

  getLocale();
  setLocale(locale: Locale);
  localizeDate(
    date: ValidDate,
    locale: Locale = this.getLocale(),
    options?: DateFormatOptions
  ): string;
  localizeNumber(
    value: number | string,
    type: NumberTypes,
    locale: Locale = this.getLocale(),
    options?: Intl.NumberFormatOptions
  ): string;
  _resolveCurrencyCode(locale: Locale = this.getLocale());
  ngOnDestroy();
}

interface LocaleFormatOptions {
  date?: DateFormatOptions;
  decimal?: NumberFormatOptions;
  currency?: NumberFormatOptions;
  percent?: NumberFormatOptions;
}

interface LocaleConfig {
  global?: LocaleFormatOptions;
  localeBased?: HashMap<LocaleFormatOptions>;
}

interface TranslocoLocaleConfig {
  defaultLocale?: Locale;
  localeConfig?: LocaleConfig;
  langToLocaleMapping?: HashMap<Locale>;
  localeToCurrencyMapping?: HashMap<Currency>;
}

interface NumberFormatOptions {
  useGrouping?: Intl.NumberFormatOptions["useGrouping"];
  minimumIntegerDigits?: Intl.NumberFormatOptions["minimumIntegerDigits"];
  minimumFractionDigits?: Intl.NumberFormatOptions["minimumFractionDigits"];
  maximumFractionDigits?: Intl.NumberFormatOptions["maximumFractionDigits"];
  minimumSignificantDigits?: Intl.NumberFormatOptions["minimumSignificantDigits"];
  maximumSignificantDigits?: number;
}

interface DateFormatOptions {
  dateStyle?: DateFormatStyles;
  timeStyle?: DateFormatStyles;
  fractionalSecondDigits?: 0 | 1 | 2 | 3;
  dayPeriod?: DateStringFormats;
  hour12?: boolean;
  localeMatcher?: "lookup" | "best fit";
  formatMatcher?: "lookup" | "best fit";
  weekday?: DateStringFormats;
  era?: DateStringFormats;
  year?: DateNumberFormats;
  month?: DateStringFormats | DateNumberFormats;
  day?: DateNumberFormats;
  hour?: DateNumberFormats;
  minute?: DateNumberFormats;
  second?: DateNumberFormats;
  timeZone?: Intl.DateTimeFormatOptions["timeZone"];
  timeZoneName?: TimezoneNameFormats;
}

const pipes: (
  | typeof TranslocoCurrencyPipe
  | typeof TranslocoDatePipe
  | typeof TranslocoDecimalPipe
  | typeof TranslocoPercentPipe
)[];

class TranslocoLocaleModule {
  static init(
    config: TranslocoLocaleConfig = {}
  ): ModuleWithProviders<TranslocoLocaleModule>;
}

class TranslocoDatePipe
  extends TranslocoLocalePipe
  implements PipeTransform, OnDestroy {
  transform(date: ValidDate, options: DateFormatOptions = {}, locale?: Locale);
  ngOnDestroy(): void;
}

class TranslocoCurrencyPipe
  extends TranslocoLocalePipe
  implements PipeTransform, OnDestroy {
  transform(
    value: number | string,
    display: "code" | "symbol" | "name" = "symbol",
    numberFormatOptions: NumberFormatOptions = {},
    currencyCode?: Currency,
    locale?: Locale
  ): string;
  ngOnDestroy(): void;
}

class TranslocoPercentPipe
  extends TranslocoLocalePipe
  implements PipeTransform, OnDestroy {
  transform(
    value: number | string,
    numberFormatOptions: NumberFormatOptions = {},
    locale?: Locale
  ): string;
  ngOnDestroy(): void;
}

class TranslocoDecimalPipe
  extends TranslocoLocalePipe
  implements PipeTransform, OnDestroy {
  transform(
    value: string | number,
    numberFormatOptions: NumberFormatOptions = {},
    locale?: Locale
  ): string;
  ngOnDestroy(): void;
}

class DefaultDateTransformer implements TranslocoDateTransformer {
  public transform(
    date: Date,
    locale: Locale,
    options: DateFormatOptions
  ): string;
}

class DefaultNumberTransformer implements TranslocoNumberTransformer {
  public transform(
    value: number | string,
    type: NumberTypes,
    locale: string,
    options: Intl.NumberFormatOptions
  ): string;
}

class MessageFormatTranspiler extends DefaultTranspiler {
